insert  into `tn_news`(`id`,`news_category_id`,`name`,`description`,`content`,`slug`,`image`,`video`,`view`,`sorted`,`source`,`start`,`created_by`,`created_at`,`updated_by`,`updated_at`,`is_comment`,`is_publish`,`publish_date`,`status`) values (1,7,'2 chung cư ở trung tâm Sài Gòn phải tháo dỡ khẩn cấp','Độ chịu lực của hai chung cư hơn 40 tuổi ở quận 1 (TP HCM) xuống cấp trầm trọng, có thể sập bất cứ lúc nào... cần di dời dân khẩn cấp','<div style=\"width: 80%\">\r\n<p style=\"text-align:justify\">UBND quận 1 vừa th&ocirc;ng b&aacute;o cho cư d&acirc;n chung cư 155-157 B&ugrave;i Viện (phường Phạm Ngũ L&atilde;o) v&agrave; chung cư 128 Hai B&agrave; Trưng (phường Đa Kao) về t&igrave;nh trạng xuống cấp, phải di dời, ph&aacute; dỡ khẩn cấp. Kế hoạch x&acirc;y dựng lại hai chung cư c&oacute; từ trước năm 1975 n&agrave;y, đang được nghi&ecirc;n cứu.</p>\r\n\r\n<p style=\"text-align:justify\">Chung cư tr&ecirc;n đường B&ugrave;i Viện gồm một trệt, 6 tầng, c&oacute; kết quả kiểm định ở mức D - khả năng chịu lực của kết cấu kh&ocirc;ng đ&aacute;p ứng được nhu cầu sử dụng b&igrave;nh thường. Nh&agrave; xuất hiện t&igrave;nh trạng nguy hiểm tổng thể.</p>\r\n\r\n<p style=\"text-align:justify\">Chung cư 3 tầng tr&ecirc;n đường Hai B&agrave; Trưng cũng c&oacute; kết quả đ&aacute;nh gi&aacute; tương tự, c&oacute; thể sập bất cứ l&uacute;c n&agrave;o.</p>\r\n\r\n<p style=\"text-align:justify\">Về vấn đề xử l&yacute; c&aacute;c chung cư cũ n&aacute;t, hồi đầu th&aacute;ng 3 UBND TP HCM ủy quyền cho c&aacute;c quận thực hiện nhiệm vụ li&ecirc;n quan đến thủ tục đầu tư cải tạo, sửa chữa, x&acirc;y dựng chung cư mới thay thế.</p>\r\n\r\n<p style=\"text-align:justify\">TP HCM hiện c&oacute; 474 chung cư cũ x&acirc;y dựng trước năm 1975 với gần 27.000 hộ sinh sống. Th&agrave;nh phố đang c&oacute; chủ trương k&ecirc;u gọi tư nh&acirc;n tham gia th&aacute;o dỡ ch&uacute;ng, do trước đ&oacute; tiến độ giải tỏa chậm - hơn 10 năm nay chỉ c&oacute; 32 chung cư được cải tạo.</p>\r\n\r\n<p style=\"text-align:justify\">Theo Duy Trần/VnExpress.net</p>\r\n</div>\r\n','2-chung-cu-o-trung-tam-sai-gon-phai-thao-do-khan-cap','/web/uploads/images/095621baoxaydung_8.jpg','',NULL,NULL,'',1,NULL,'2016-11-20 21:49:22',NULL,'2016-11-20 21:49:29',0,1,NULL,1),(2,7,'Ngôi nhà thiết kế vô duyên gây xôn xao vì giá thuê quá đắt','Ngôi nhà thiết kế vô duyên gây xôn xao vì giá thuê quá đắt','<p><span style=\"color:rgb(0, 0, 0); font-family:arial,helvetica,sans-serif; font-size:12px\">Th&agrave;nh phố San Francisco (Mỹ) l&agrave; nơi c&oacute; gi&aacute; nh&agrave; cho thu&ecirc; kh&aacute; cao n&ecirc;n người d&acirc;n phải bỏ ra nhiều tiền chỉ để ở trong căn hộ nhỏ l&agrave; chuyện b&igrave;nh thường. Tuy nhi&ecirc;n, suốt tuần qua, căn nh&agrave; hơn 18 m2 được rao 2.000 USD một th&aacute;ng đ&atilde; g&acirc;y n&ecirc;n sự ch&uacute; &yacute; của cả b&aacute;o ch&iacute; v&agrave; cộng đồng mạng. Điểm khiến mọi người sốc nhất l&agrave; c&aacute;ch bố tr&iacute; khu tắm đứng, bồn cầu ngay trong khu nấu nướng. Điều n&agrave;y đồng nghĩa, nếu nh&agrave; c&oacute; hai người, bạn sẽ kh&ocirc;ng thể đi WC hoặc tắm khi c&oacute; người đang nấu nướng</span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.baoxaydung.com.vn/stores/news_dataimages/vananh/042017/14/14/142337baoxaydung_30.jpg\" style=\"height:375px; margin:0px; padding:0px; width:500px\" /></p>\r\n\r\n<p>Căn hộ nhỏ n&ecirc;n ph&ograve;ng ngủ ki&ecirc;m ph&ograve;ng kh&aacute;ch với bộ sofa v&agrave;o ban ng&agrave;y v&agrave; giường gập sẽ được mở ra v&agrave;o buổi tối.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.baoxaydung.com.vn/stores/news_dataimages/vananh/042017/14/14/142337baoxaydung_31.jpg\" style=\"height:375px; margin:0px; padding:0px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:justify\">Nh&agrave; c&oacute; t&ocirc;ng m&agrave;u đen-trắng-gỗ kh&aacute; nh&atilde; nhặn, c&aacute;c trang thiết bị hiện đại, tủ đồ nhiều chỗ chứa.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://www.baoxaydung.com.vn/stores/news_dataimages/vananh/042017/14/14/142337baoxaydung_32.jpg\" style=\"height:375px; margin:0px; padding:0px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:justify\">Gia chủ kh&ocirc;ng cung cấp nhiều h&igrave;nh ảnh, th&ocirc;ng tin về nh&agrave; nhưng căn hộ tr&ocirc;ng vẫn c&ograve;n đẹp như vừa mới được x&acirc;y. Hiện tại, nh&agrave; vẫn chưa c&oacute; người thu&ecirc;.</p>\r\n\r\n<p style=\"text-align:justify\">Theo Lam Huyền (Ảnh: House Beautiful)/VnExpress.net</p>\r\n\r\n<p>&nbsp;</p>\r\n','ngoi-nha-thiet-ke-vo-duyen-gay-xon-xao-vi-gia-thue-qua-dat','/web/uploads/images/142337baoxaydung_29.jpg','',NULL,NULL,'',0,NULL,'2016-11-21 21:49:35',NULL,'2016-11-21 21:49:38',0,1,NULL,1),(4,7,'Tòa nhà chọc trời đưa cư dân đi khắp thế giới trong 24h','Tòa nhà chọc trời Analemma Tower một khi được xây dựng, sẽ được “neo” dưới một thiên thạch cách Trái đất 50.000km và cư dân có thể tận hưởng khung cảnh thay đổi liên tục bên ngoài trong 24 giờ.','<p style=\"text-align:justify\">Theo Daily Mail, c&ocirc;ng ty kiến tr&uacute;c Clouds Architecture Office ở New York tự tin v&agrave;o kế hoạch x&acirc;y t&ograve;a nh&agrave; chọc trời độc đ&aacute;o bậc nhất từ trước đến nay.</p>\r\n\r\n<p style=\"text-align:justify\">Cụ thể, Analemma Tower sẽ được x&acirc;y dựng từ tr&ecirc;n trời xuống đất, thay v&igrave; x&acirc;y dựng tr&ecirc;n mặt đất như th&ocirc;ng thường.</p>\r\n\r\n<p style=\"text-align:justify\">&ldquo;Analemma Tower l&agrave; một v&iacute; dụ điển h&igrave;nh của việc sử dụng chiến lược thiết kế h&agrave;nh tinh, tạo n&ecirc;n t&ograve;a nh&agrave; cao nhất thế giới từ trước đến nay&rdquo;, đại diện c&ocirc;ng ty n&oacute;i.</p>\r\n\r\n<p style=\"text-align:justify\">Trong bản thiết kế, c&ocirc;ng ty sử dụng một hệ thống c&aacute;p treo cỡ lớn, một đầu m&oacute;c v&agrave;o tiểu h&agrave;nh tinh&nbsp;c&ograve;n đầu kia giữ cho t&ograve;a nh&agrave; chọc trời kh&ocirc;ng rơi xuống đất.</p>\r\n\r\n<p style=\"text-align:justify\">&ldquo;V&igrave; t&ograve;a nh&agrave; n&agrave;y treo lơ lửng trong kh&ocirc;ng kh&iacute; n&ecirc;n n&oacute; c&oacute; thể được x&acirc;y dựng ở bất cứ đ&acirc;u tr&ecirc;n thế giới, sau đ&oacute; được đưa đến địa điểm lắp đặt&rdquo;, Clouds Architecture Office chia sẻ tr&ecirc;n trang web.</p>\r\n\r\n<p style=\"text-align:justify\">&ldquo;Theo kế hoạch ban đầu, Analemma Tower c&oacute; thể được x&acirc;y dựng ở Dubai, nơi c&oacute; c&aacute;c chuy&ecirc;n gia thiết kế nh&agrave; chọc trời với chi ph&iacute; chỉ bằng một phần năm ở New York&rdquo;.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/web/uploads/images/1490674562-149067184351595-toa-nha-6.jpg\" style=\"height:402px; width:700px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Tiểu h&agrave;nh tinh c&oacute; quỹ đạo cố định h&igrave;nh số 8.</em></p>\r\n\r\n<p style=\"text-align:justify\">Mặc d&ugrave; kế hoạch t&aacute;o bạo n&agrave;y vướng phải những th&aacute;ch thức như việc t&igrave;m kiếm một thi&ecirc;n thạch ph&ugrave; hợp để neo giữ t&ograve;a nh&agrave;, Clouds Architecture Office tin rằng, &yacute; tưởng n&agrave;y sẽ sớm trở th&agrave;nh hiện thực.</p>\r\n\r\n<p style=\"text-align:justify\">&ldquo;Năm 2015, Cơ quan H&agrave;ng kh&ocirc;ng vũ trụ ch&acirc;u &Acirc;u đ&atilde; khởi động chiến dịch thu h&uacute;t đầu tư, khai th&aacute;c tiểu h&agrave;nh tinh. NASA đặt mục ti&ecirc;u bắt giữ một tiểu h&agrave;nh tinh v&agrave;o năm 2021 để chứng minh t&iacute;nh khả thi&rdquo;, trang web của c&ocirc;ng ty kiến tr&uacute;c Mỹ n&oacute;i.</p>\r\n\r\n<p style=\"text-align:justify\">Nếu được x&acirc;y dựng, t&ograve;a nh&agrave; chọc trời sẽ di chuyển theo h&igrave;nh số 8, dựa tr&ecirc;n quỹ đạo tiểu h&agrave;nh tinh trong v&ograve;ng 24 giờ, đưa cư d&acirc;n qua c&aacute;c th&agrave;nh phố lớn ở Bắc v&agrave; Nam b&aacute;n cầu như New York, Havana, Atlanta v&agrave; Panama.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/web/uploads/images/1490674562-149067162138430-toa-nha-5.jpg\" style=\"height:1200px; width:339px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Ph&aacute;c họa kế hoạch t&aacute;o bạo của&nbsp;Cloud Architecture Office.</em></p>\r\n\r\n<p style=\"text-align:justify\">Ở đỉnh t&ograve;a nh&agrave; Analemma Tower, qu&atilde;ng thời gian ban ng&agrave;y tăng th&ecirc;m 40 ph&uacute;t do t&aacute;c động từ độ cong của Tr&aacute;i đất.</p>\r\n\r\n<p style=\"text-align:justify\">Trong bản thiết kế, Cloud Architecture Office kh&ocirc;ng chỉ muốn x&acirc;y dựng t&ograve;a nh&agrave; chọc trời khổng lồ, m&agrave; c&ograve;n cả một th&agrave;nh phố tr&ocirc;i nổi tr&ecirc;n bầu trời.</p>\r\n\r\n<p style=\"text-align:justify\">Một khu vực rộng lớn b&ecirc;n trong t&ograve;a nh&agrave; sẽ được thiết kế l&agrave;m chỗ nghỉ cho cư d&acirc;n, khiến họ cảm thấy như ở nh&agrave;. Văn ph&ograve;ng l&agrave;m việc được đặt ở phần dưới trong khi nơi nghỉ ngơi nằm ở nửa tr&ecirc;n.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/web/uploads/images/1490674562-14906713437144-toa-nha-2.jpg\" style=\"height:500px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Cư d&acirc;n sống tại t&ograve;a nh&agrave; sẽ kh&ocirc;ng được ph&eacute;p ra ngo&agrave;i h&oacute;ng gi&oacute; v&igrave; l&yacute; do an to&agrave;n.</em></p>\r\n\r\n<p style=\"text-align:justify\">Cư d&acirc;n sống tại đ&acirc;y cũng được tận hưởng khu vườn nh&acirc;n tạo, nơi phục vụ t&iacute;n ngưỡng v&agrave; khu vực ăn uống, giải tr&iacute;.</p>\r\n\r\n<p style=\"text-align:justify\">Cloud Architecture Office c&oacute; kế hoạch tận dụng tối đa vị tr&iacute; đặc biệt của t&ograve;a nh&agrave;, với những tấm pin Mặt trời đặt ở tầng cao nhất để tạo n&ecirc;n nguồn năng lượng từ &aacute;nh s&aacute;ng Mặt trời.</p>\r\n\r\n<p style=\"text-align:justify\">Nguồn nước sạch được lọc từ đ&aacute;m m&acirc;y v&agrave; nước mưa, đảm bảo độ tinh khiết cao nhất. C&aacute;c kiến tr&uacute;c sư cũng t&iacute;nh đến việc thiết kế cửa k&iacute;nh c&oacute; độ d&agrave;y kh&aacute;c nhau, ph&ugrave; hợp với sức &eacute;p v&agrave; nhiệt độ ở độ cao nhất định.</p>\r\n\r\n<p style=\"text-align:justify\">Tuy vậy, cư d&acirc;n nhiều khả năng sẽ kh&ocirc;ng được ph&eacute;p ra ngo&agrave;i h&oacute;ng m&aacute;t v&igrave; điều kiện khắc nghiệt ở tr&ecirc;n cao. &ldquo;Ở độ cao 32.000 m&eacute;t, kh&ocirc;ng kh&iacute; rất lo&atilde;ng, nhiệt độ &acirc;m 40 độ C sẽ khiến cư d&acirc;n kh&ocirc;ng thể ra ngo&agrave;i m&agrave; kh&ocirc;ng c&oacute; đồ bảo hộ&rdquo;,&nbsp; Clouds Architecture Office cho biết.</p>\r\n\r\n<p style=\"text-align: right;\"><strong>Theo Đăng Nguyễn - Daily Mail (D&acirc;n Việt)</strong></p>\r\n','toa-nha-choc-troi-dua-cu-dan-di-khap-the-gioi-trong-24h','/web/uploads/images/1490674562-149067129763960-toa-nha-1.jpg','',NULL,NULL,'http://vnexpress.net',1,NULL,NULL,NULL,NULL,0,1,NULL,1),(6,10,'Quy trình bàn giao căn hộ khách hàng nào cũng nên biết','','<p>Nhận b&agrave;n giao căn hộ/ nh&agrave; phố l&agrave; c&ocirc;ng việc rất dễ xảy ra sai s&oacute;t, nhất l&agrave; những người mua nh&agrave; lần đầu. Dưới đ&acirc;y l&agrave; quy tr&igrave;nh 10 bước, do &ocirc;ng Nguyễn Duy Th&agrave;nh, Tổng gi&aacute;m đốc C&ocirc;ng ty Quản l&yacute; nh&agrave; To&agrave;n Cầu (Global Home), tổng hợp từ kinh nghiệm thực tế.</p>\r\n\r\n<p>Bước 1: Nghiệm thu điều h&ograve;a</p>\r\n\r\n<p>Đầu ti&ecirc;n, đ&oacute;ng tất cả cửa ph&ograve;ng lại v&agrave; bật điều h&ograve;a l&ecirc;n để nhiệt độ khoảng 22 độ. Đếm số lượng điều h&ograve;a, số lượng cục n&oacute;ng.</p>\r\n\r\n<p>- Tất cả c&aacute;c ph&ograve;ng đều m&aacute;t, v&agrave; phải ngắt lốc sau tối đa 2 tiếng, aptomat kh&ocirc;ng nhảy.</p>\r\n\r\n<p>- Kh&ocirc;ng chảy nước xuống s&agrave;n tại miệng thổi v&agrave; miệng hồi.</p>\r\n\r\n<p>- Lắng nghe khi cục n&oacute;ng chạy c&oacute; ph&aacute;t ra tiếng ồn qu&aacute; to kh&ocirc;ng. Nếu ồn th&igrave; phải kiểm tra những bước sau:</p>\r\n\r\n<p>+ Kiểm tra c&aacute;c tấm vận chuyển đ&atilde; th&aacute;o chưa.</p>\r\n\r\n<p>+ Kiểm tra đường ống c&oacute; bị chạm v&agrave;o vỏ m&aacute;y kh&ocirc;ng.</p>\r\n\r\n<p>+ Kiểm tra xem cục n&oacute;ng c&oacute; bị nghi&ecirc;ng, xi&ecirc;u vẹo hay kh&ocirc;ng. C&aacute;c bulong ốc v&iacute;t c&oacute; lắp đủ v&agrave; chặt kh&ocirc;ng.</p>\r\n\r\n<p>Để đảm bảo qu&aacute; tr&igrave;nh thi c&ocirc;ng đ&uacute;ng kỹ thuật v&agrave; chủng loại vật tư, chủ nh&agrave; n&ecirc;n thu&ecirc; C&ocirc;ng ty tư vấn gi&aacute;m s&aacute;t nh&agrave; phố chuy&ecirc;n nghiệp để gi&aacute;m s&aacute;t qu&aacute; tr&igrave;nh thi c&ocirc;ng n&agrave;y</p>\r\n\r\n<p>b&agrave;n giao căn hộ, quy tr&igrave;nh b&agrave;n giao căn hộ, nhận b&agrave;n giao căn hộ<br />\r\nNhận b&agrave;n giao căn hộ l&agrave; c&ocirc;ng việc rất dễ xảy ra sai s&oacute;t, do đ&oacute; kh&aacute;ch h&agrave;ng phải kiểm tra kĩ lưỡng t&igrave;nh trạng căn hộ trước khi nhận b&agrave;n giao. (Ảnh minh họa: Internet)<br />\r\nBước 2: C&aacute;c thiết bị điện, viễn th&ocirc;ng</p>\r\n\r\n<p>- Kiểm tra tủ điện: C&aacute;c aptomat phải ở t&igrave;nh trạng giật xuống l&agrave; tắt, k&eacute;o l&ecirc;n l&agrave; bật.</p>\r\n\r\n<p>- Kiểm tra ổ cắm để xem t&igrave;nh trạng c&oacute; điện của c&aacute;c ổ.</p>\r\n\r\n<p>- Kiểm tra quạt gi&oacute; vệ sinh, m&aacute;y h&uacute;t m&ugrave;i: Nhanh nhất l&agrave; ch&acirc;m điếu thuốc l&aacute;, h&iacute;t một hơi, phả kh&oacute;i v&agrave;o gần quạt rồi theo d&otilde;i xem tốc độ h&uacute;t.</p>\r\n\r\n<p>- Tắt mở c&ocirc;ng tắc kiểm tra b&oacute;ng đ&egrave;n, y&ecirc;u cầu s&aacute;ng đều kh&ocirc;ng bị nh&aacute;y.</p>\r\n\r\n<p>- D&ugrave;ng b&uacute;t thử kiểm tra r&ograve; điện. N&ecirc;n kiểm tra ngẫu hứng tr&ecirc;n tường, khu xung quanh c&ocirc;ng tắc v&agrave; ổ điện.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, với hạng mục thiết bị điện, viễn th&ocirc;ng đ&ograve;i hỏi người c&oacute; chuy&ecirc;n m&ocirc;n kiểm tra, nghiệm thu trong suốt qu&aacute; tr&igrave;nh thi c&ocirc;ng, nếu chủ nh&agrave; c&oacute; điều kiện n&ecirc;n thu&ecirc; C&ocirc;ng ty tư vấn gi&aacute;m s&aacute;t nh&agrave; phố chuy&ecirc;n nghiệp để gi&aacute;m s&aacute;t qu&aacute; tr&igrave;nh thi c&ocirc;ng n&agrave;y, chi ph&iacute; thu&ecirc; gi&aacute;m s&aacute;t nh&agrave; phố hiện nay kh&aacute; rẻ, bạn c&oacute; thể tham khảo TẠI Đ&Acirc;Y&nbsp;</p>\r\n\r\n<p>Bước 3: Kiểm tra tường, trần</p>\r\n\r\n<p>- Nh&igrave;n m&agrave;u sơn phải đồng đều, kh&ocirc;ng loang lổ, lưu &yacute; c&aacute;c chỗ quanh c&ocirc;ng tắc, miệng điều h&ograve;a, quạt gi&oacute; l&agrave; hay lỗi nhất.</p>\r\n\r\n<p>- Kiểm tra độ phẳng của tường: d&ugrave;ng thước nh&ocirc;m &aacute;p l&ecirc;n tường v&agrave; nh&igrave;n qua khe hở giữa thước v&agrave; tường, tắt đ&egrave;n v&agrave; d&ugrave;ng đ&egrave;n pin soi ngược lại xem &aacute;nh s&aacute;ng lọt ngược lại. Kiểm tra đặc biệt ở cao độ khoảng 1,5m đến 1,8m (chỗ gi&aacute;p lai giữa hai tầm gi&aacute;o) l&agrave; hay bị lỗi nhất.</p>\r\n\r\n<p>Bước 4 : Kiểm tra s&agrave;n gỗ, s&agrave;n gạch</p>\r\n\r\n<p>Y&ecirc;u cầu ghi r&otilde; chủng loại, xuất xứ của sản phẩm lắp đặt, cung cấp c&aacute;c th&ocirc;ng số kỹ thuật, đặc t&iacute;nh của vật liệu, giấy bảo h&agrave;nh v&agrave; thời hạn bảo h&agrave;nh.</p>\r\n\r\n<p>Phần s&agrave;n gỗ:</p>\r\n\r\n<p>- M&agrave;u sắc: đều m&agrave;u, kiểm tra lớp phủ v&acirc;n bề mặt, nếu xước, lỗi bắt thay.</p>\r\n\r\n<p>- Kiểm tra độ phẳng của s&agrave;n bằng thước nh&ocirc;m, kết hợp đ&egrave;n pin như kiểm tra tường, nếu kh&ocirc;ng phẳng, y&ecirc;u cầu sửa lại.</p>\r\n\r\n<p>- Kiểm tra c&aacute;c khe hở giữa 2 tấm gỗ, nếu hở lớn, y&ecirc;u cầu sửa lại.</p>\r\n\r\n<p>- Kiểm tra to&agrave;n bộ mặt s&agrave;n xem c&oacute; chỗ n&agrave;o phồng, rộp kh&ocirc;ng (ch&uacute; &yacute; c&aacute;c g&oacute;c nh&agrave;, cạnh tường v&agrave; cửa toilet), nếu c&oacute; lỗi, y&ecirc;u cầu sửa.</p>\r\n\r\n<p>- Đi lại tr&ecirc;n s&agrave;n phải kh&ocirc;ng c&oacute; tiếng cọt kẹt, cứ đi lại v&agrave; nh&uacute;n tr&ecirc;n s&agrave;n th&igrave; sẽ ph&aacute;t hiện ra.</p>\r\n\r\n<p>- Kiểm tra nẹp ch&acirc;n tường: Phải đồng mầu, chỗ nối đấu đầu nẹp ch&acirc;n tường phải phẳng, kh&ocirc;ng nh&igrave;n thấy nốt đinh tr&ecirc;n mặt nẹp, c&aacute;c đoạn nối kh&ocirc;ng được qu&aacute; ngắn, phải &gt; 1,5 - 2m cho mỗi đoạn, chỗ tiếp gi&aacute;p giữa nẹp chận tường v&agrave; s&agrave;n gỗ kh&ocirc;ng được hở khe theo mặt bằng v&agrave; mặt đứng. Nếu hở mặt bằng tức l&agrave; s&agrave;n gỗ bị hụt, hở mặt đứng l&agrave; nẹp ch&acirc;n tường bị lẹm hoặc cong.</p>\r\n\r\n<p>Nếu bạn thấy kh&oacute; khăn trong vấn đề n&agrave;y, th&igrave; c&oacute; thể khi nhận b&agrave;n giao nh&agrave; h&atilde;y dẫn theo người c&oacute; chuy&ecirc;n m&ocirc;n v&agrave; kinh nghiệm để tư vấn cho bạn, hoặc cũng c&oacute; thể thu&ecirc; C&ocirc;ng ty tư vấn gi&aacute;m s&aacute;t nh&agrave; phố chuy&ecirc;n nghiệp uy t&iacute;n hỗ trợ bạn, tại Tphcm c&oacute; C&ocirc;ng ty tư vấn gi&aacute;m s&aacute;t nh&agrave; phố uy t&iacute;n NhaPhoGroup, xem &nbsp;TẠI Đ&Acirc;Y</p>\r\n\r\n<p>Phần gạch ốp:</p>\r\n\r\n<p>C&aacute;c mạch ốp phải thẳng, đều v&agrave; sắc n&eacute;t. To&agrave;n bộ bề mặt kết cấu phải l&agrave;m sạch vữa, bột tr&aacute;t mạch v&agrave; c&aacute;c vết bẩn ố tr&ecirc;n bề mặt ốp. C&ocirc;ng t&aacute;c kiểm tra chất lượng ốp tiến h&agrave;nh theo tr&igrave;nh tự thi c&ocirc;ng v&agrave; bao gồm c&aacute;c chỉ ti&ecirc;u ch&iacute;nh như sau:</p>\r\n\r\n<p>- Độ phẳng của mặt ốp, l&agrave;m tương tự như kiểm tra tường.</p>\r\n\r\n<p>- Độ đặc chắc v&agrave; b&aacute;m d&iacute;nh của nền ốp với vật liệu ốp - g&otilde; v&agrave;o bề mặt gạch ốp.</p>\r\n\r\n<p>- Độ đồng đều của của mặt ốp về m&agrave;u sắc, hoa văn, c&aacute;c mạch ốp, chi tiết trang tr&iacute;.</p>\r\n\r\n<p>- Đối với c&aacute;c ph&ograve;ng kh&aacute;c cũng kiểm tra tương tự, về độ phẳng, đồng đều m&agrave;u sắc, kh&ocirc;ng bị nứt, vỡ, c&aacute;c mạch gạch phải được đầy v&agrave; c&ugrave;ng m&agrave;u s&agrave;n gạch.</p>\r\n\r\n<p>Mặt ốp phải thoả m&atilde;n c&aacute;c y&ecirc;u cầu:</p>\r\n\r\n<p>- Tổng thể mặt ốp phải đảm bảo đ&uacute;ng h&igrave;nh d&aacute;ng, k&iacute;ch thước h&igrave;nh học theo y&ecirc;u cầu của thiết kế.</p>\r\n\r\n<p>- Vật liệu ốp phải đ&uacute;ng quy c&aacute;ch về chủng loại, k&iacute;ch thước v&agrave; m&agrave;u sắc, kh&ocirc;ng cong v&ecirc;nh, sứt mẻ, kh&ocirc;ng c&oacute; khuyết tật</p>\r\n\r\n<p>- M&agrave;u sắc của mặt ốp bằng vật liệu nh&acirc;n tạo phải đồng nhất.</p>\r\n\r\n<p>- C&aacute;c mạch ốp ngang dọc phải sắc n&eacute;t, đều thẳng v&agrave; đầy vữa.</p>\r\n\r\n<p>- Vữa tr&aacute;t tr&ecirc;n kết cấu phải chắc đặc. Khi kiểm tra vỗ l&ecirc;n mặt ốp kh&ocirc;ng c&oacute; tiếng bộp. Những vi&ecirc;n bị bộp v&agrave; long ch&acirc;n phải th&aacute;o ra ốp lại.</p>\r\n\r\n<p>- Tr&ecirc;n mặt ốp kh&ocirc;ng được c&oacute; vết nứt, vết ố do vữa, sơn, v&ocirc;i hoặc do c&aacute;c loại ho&aacute; chất g&acirc;y ra.</p>\r\n\r\n<p>- Khi kiểm tra bằng thước d&agrave;i 2m đặt &aacute;p s&aacute;t v&agrave;o mặt ốp, khe hở giữa thước v&agrave; mặt ốp kh&ocirc;ng qu&aacute; 2mm.</p>\r\n\r\n<p>Cần sự hỗ trợ, c&aacute;c bạn vui l&ograve;ng li&ecirc;n hệ &nbsp;C&ocirc;ng ty tư vấn gi&aacute;m s&aacute;t nh&agrave; phố NhaPhoGroup</p>\r\n','quy-trinh-ban-giao-can-ho-khach-hang-nao-cung-nen-biet','','',NULL,NULL,'',0,NULL,NULL,NULL,NULL,0,0,NULL,1),(9,7,'Gỡ vướng cho gần 11000 căn nhà không đủ điều kiện bán','Qua 20 năm thực hiện việc bán nhà thuộc sở hữu Nhà nước theo chỉ đạo của Chính phủ, Hà Nội đã bán và cấp giấy chứng nhận cho hơn 154.000 căn nhà, với doanh thu đạt trên 5.000 tỷ đồng.','<p style=\"text-align:justify\">Mặc d&ugrave; quy tr&igrave;nh b&aacute;n nh&agrave; đ&atilde; được sửa đổi, bổ sung ba lần theo hướng r&uacute;t ngắn thời gian thụ l&yacute; hồ sơ v&agrave; giảm c&aacute;c thủ tục h&agrave;nh ch&iacute;nh; c&ocirc;ng khai gi&aacute; b&aacute;n cũng như c&aacute;c điều kiện nh&agrave; ở được b&aacute;n v&agrave; nh&agrave; ở thuộc diện kh&ocirc;ng được b&aacute;n, c&ugrave;ng với nhiều chế độ, ch&iacute;nh s&aacute;ch miễn giảm tiền nh&agrave;, tiền sử dụng đất&hellip;, nhưng thực tế cho thấy, việc b&aacute;n nh&agrave; v&agrave; cấp giấy chứng nhận cho người mua nh&agrave; ở thuộc sở hữu Nh&agrave; nước tr&ecirc;n địa b&agrave;n th&agrave;nh phố vẫn c&ograve;n chậm, tồn tại nhiều vướng mắc.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Gần 11.000 căn nh&agrave; kh&ocirc;ng đủ điều kiện b&aacute;n</strong></p>\r\n\r\n<p style=\"text-align:justify\">Theo Sở X&acirc;y dựng H&agrave; Nội, hiện C&ocirc;ng ty Tr&aacute;ch nhiệm hữu hạn một th&agrave;nh vi&ecirc;n Quản l&yacute; v&agrave; Ph&aacute;t triển nh&agrave; H&agrave; Nội đang quản l&yacute; gần 14.000 căn hộ; trong đ&oacute; gần 3.000 căn nh&agrave; thuộc diện được b&aacute;n đ&atilde; c&oacute; đơn v&agrave; hồ sơ mua nh&agrave;, c&ograve;n lại gần 11.000 căn thuộc diện kh&ocirc;ng đủ điều kiện để b&aacute;n.</p>\r\n\r\n<p style=\"text-align:justify\">Theo kết luận mới đ&acirc;y của Đo&agrave;n Gi&aacute;m s&aacute;t Hội đồng Nh&acirc;n d&acirc;n th&agrave;nh phố H&agrave; Nội, nhiều trường hợp hộ gia đ&igrave;nh thuộc diện đủ điều kiện được mua nh&agrave; v&agrave; đ&atilde; được đơn vị quản l&yacute; nh&agrave; tiếp nhận hồ sơ từ năm 2015 nhưng chưa được xem x&eacute;t, giải quyết b&aacute;n. Nhiều trường hợp c&oacute; vướng mắc chưa được C&ocirc;ng ty Tr&aacute;ch nhiệm hữu hạn một th&agrave;nh vi&ecirc;n Quản l&yacute; v&agrave; Ph&aacute;t triển nh&agrave; H&agrave; Nội v&agrave; c&aacute;c cơ quan chức năng kiểm tra, th&aacute;o gỡ kịp thời, g&acirc;y bức x&uacute;c trong dư luận nh&acirc;n d&acirc;n.</p>\r\n\r\n<p style=\"text-align:justify\">Đặc biệt, hơn bảy th&aacute;ng qua, từ khi c&oacute; Chỉ thị 09-CT/TU của Th&agrave;nh ủy v&agrave; Chỉ thị 11/CT-Ủy ban Nh&acirc;n d&acirc;n ng&agrave;y 17/5/2016 của Ủy ban Nh&acirc;n d&acirc;n th&agrave;nh phố về đẩy nhanh tiến độ b&aacute;n nh&agrave; thuộc sở hữu Nh&agrave; nước v&agrave; cấp giấy chứng nhận, c&aacute;c đơn vị chức năng mới giải quyết b&aacute;n được 148 căn. Kh&ocirc;ng những vậy, việc quản l&yacute;, thống k&ecirc;, theo d&otilde;i quỹ nh&agrave; ở n&agrave;y c&ograve;n hạn chế; kh&ocirc;ng c&oacute; số liệu ch&iacute;nh thức về số lượng nh&agrave; ở cũ do c&aacute;c cơ quan tự quản của trung ương v&agrave; H&agrave; Nội chưa b&agrave;n giao về Sở X&acirc;y dựng quản l&yacute;.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Thiếu tr&aacute;ch nhiệm trong xử l&yacute;, giải quyết</strong></p>\r\n\r\n<p style=\"text-align:justify\">&Ocirc;ng Nguyễn Ngọc Tuấn, Ph&oacute; Chủ tịch thường trực, Hội đồng Nh&acirc;n d&acirc;n th&agrave;nh phố H&agrave; Nội - Ph&oacute; trưởng đo&agrave;n gi&aacute;m s&aacute;t, cho biết nhiều nguy&ecirc;n nh&acirc;n dẫn đến những tồn tại, bất cập trong c&ocirc;ng t&aacute;c b&aacute;n nh&agrave; thuộc sở hữu Nh&agrave; nước. Tuy nhi&ecirc;n, tr&aacute;ch nhiệm trước hết thuộc về C&ocirc;ng ty Tr&aacute;ch nhiệm hữu hạn một th&agrave;nh vi&ecirc;n Quản l&yacute; v&agrave; Ph&aacute;t triển nh&agrave; H&agrave; Nội. C&ocirc;ng ty n&agrave;y đ&atilde; kh&ocirc;ng thực hiện tốt chức năng, nhiệm vụ được giao đối với c&aacute;c trường hợp thuộc diện đủ điều kiện được b&aacute;n; thiếu quyết liệt trong giải quyết c&aacute;c trường hợp c&ograve;n vướng mắc. B&ecirc;n cạnh đ&oacute;, c&ocirc;ng t&aacute;c phối hợp giữa Sở X&acirc;y dựng với ch&iacute;nh quyền c&aacute;c địa phương v&agrave; Sở T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường trong quản l&yacute;, k&ecirc; khai, x&eacute;t cấp giấy chứng nhận cho c&aacute;c căn hộ thuộc diện nh&agrave; tự quản cũng chưa hiệu quả.</p>\r\n\r\n<p style=\"text-align:justify\">Do đ&oacute;, Đo&agrave;n Gi&aacute;m s&aacute;t đ&atilde; kiến nghị Ủy ban Nh&acirc;n d&acirc;n th&agrave;nh phố chỉ đạo r&agrave; so&aacute;t lại chức năng, nhiệm vụ, hiệu quả hoạt động, m&ocirc; h&igrave;nh tổ chức của C&ocirc;ng Tr&aacute;ch nhiệm hữu hạn một th&agrave;nh vi&ecirc;n Quản l&yacute; v&agrave; Ph&aacute;t triển nh&agrave; H&agrave; Nội; xem x&eacute;t tr&aacute;ch nhiệm c&aacute; nh&acirc;n, tập thể để hồ sơ tồn đọng k&eacute;o d&agrave;i hoặc để xảy ra vi phạm trong việc b&aacute;n nh&agrave;.</p>\r\n\r\n<p style=\"text-align:justify\">Ủy ban Nh&acirc;n d&acirc;n th&agrave;nh phố cũng chỉ đạo c&aacute;c đơn vị chức năng đẩy nhanh việc b&aacute;n nh&agrave;, cấp giấy chứng nhận đối với c&aacute;c trường hợp đủ điều kiện được b&aacute;n, phấn đấu cơ bản ho&agrave;n th&agrave;nh trong năm nay. C&ugrave;ng với đ&oacute;, thực hiện tổng kiểm tra, thống k&ecirc; đầy đủ quỹ nh&agrave; thuộc sở hữu Nh&agrave; nước, quỹ nh&agrave; ở tập thể tự quản thuộc c&aacute;c cơ quan trung ương-quốc ph&ograve;ng b&agrave;n giao về địa phương; ph&acirc;n cấp cho c&aacute;c quận, huyện quản l&yacute; nhằm n&acirc;ng cao hiệu quả, gắn tr&aacute;ch nhiệm của ch&iacute;nh quyền cơ sở.</p>\r\n\r\n<p style=\"text-align:justify\">Th&agrave;nh phố cần r&agrave; so&aacute;t, kiểm tra c&aacute;c dự &aacute;n đầu tư đ&atilde; c&oacute; quyết định thu hồi đất, nhưng qua nhiều năm chưa giải ph&oacute;ng mặt bằng, c&aacute;c diện t&iacute;ch đất vướng về quy hoạch để c&oacute; giải ph&aacute;p th&aacute;o gỡ đảm bảo quy định ph&aacute;p luật l&agrave;m căn cứ để cấp giấy chứng nhận. Trước mắt, đẩy nhanh việc r&agrave; so&aacute;t tại dự &aacute;n Nam Đại Cồ Việt, quận Hai B&agrave; Trưng.</p>\r\n\r\n<p style=\"text-align:justify\">Theo &ocirc;ng Nguyễn Ch&iacute; Dũng, Ph&oacute; Gi&aacute;m đốc, Sở X&acirc;y dựng H&agrave; Nội, việc giải quyết c&aacute;c thủ tục b&aacute;n nh&agrave; của C&ocirc;ng ty Tr&aacute;ch nhiệm hữu hạn một th&agrave;nh vi&ecirc;n Quản l&yacute; v&agrave; Ph&aacute;t triển nh&agrave; H&agrave; Nội c&ograve;n chậm, kh&ocirc;ng đảm bảo thời gian theo quy tr&igrave;nh b&aacute;n nh&agrave; l&agrave; 45 ng&agrave;y l&agrave;m việc; chưa chủ động b&aacute;o c&aacute;o c&aacute;c kh&oacute; khăn, vướng mắc v&agrave; đề xuất hướng giải quyết, th&aacute;o gỡ.</p>\r\n\r\n<p style=\"text-align:justify\">Tuy nhi&ecirc;n, b&ecirc;n cạnh những nguy&ecirc;n nh&acirc;n chủ quan, &ocirc;ng Dũng cho biết th&ecirc;m, việc thay đổi cơ chế gi&aacute; b&aacute;n, tr&igrave;nh tự thủ tục, hồ sơ, thẩm quyền giải quyết; nhiều hồ sơ phức tạp như c&oacute; tranh chấp, vướng quy hoạch, đ&atilde; c&oacute; quyết định thu hồi, nh&agrave; cổ v&agrave; nh&agrave; biệt thự kh&ocirc;ng được b&aacute;n&hellip; cũng g&acirc;y ảnh hưởng đến tiến độ b&aacute;n nh&agrave; của th&agrave;nh phố.</p>\r\n\r\n<p style=\"text-align:justify\">Đối với quỹ nh&agrave; c&oacute; nguồn gốc thuộc sở hữu Nh&agrave; nước do c&aacute;c cơ quan tự quản trung ương v&agrave; th&agrave;nh phố quản l&yacute; trước đ&acirc;y, do bu&ocirc;ng lỏng n&ecirc;n hồ sơ gốc bị thất lạc từ nhiều năm hay nhiều cơ quan đ&atilde; giải thể, s&aacute;p nhập. Nhiều cơ quan kh&ocirc;ng hợp t&aacute;c để ho&agrave;n tất thủ tục b&agrave;n giao, b&aacute;n nh&agrave; cho c&aacute;c hộ gia đ&igrave;nh v&agrave; một số khu vực vướng quy hoạch, lấn chiếm đất c&ocirc;ng, sử dụng đất sai mục đ&iacute;ch cần phải xử l&yacute; hoặc sắp xếp theo Quyết định số 09/QĐ-TTg.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Quyết liệt th&aacute;o gỡ vướng mắc</strong></p>\r\n\r\n<p style=\"text-align:justify\">Trong tổng số gần 3.000 căn nh&agrave; đ&atilde; c&oacute; hồ sơ mua nh&agrave; v&agrave; nh&agrave; ở đ&oacute; thuộc diện để b&aacute;n, Sở X&acirc;y dựng y&ecirc;u cầu, trong năm nay, C&ocirc;ng ty Tr&aacute;ch nhiệm hữu hạn một th&agrave;nh vi&ecirc;n Quản l&yacute; v&agrave; Ph&aacute;t triển nh&agrave; H&agrave; Nội phải giải quyết xong 2.199 căn đ&atilde; c&oacute; gi&aacute; b&aacute;n. C&oacute; 231 căn cần được x&aacute;c định gi&aacute; b&aacute;n, Sở sẽ tr&igrave;nh Ủy ban Nh&acirc;n d&acirc;n th&agrave;nh phố ph&ecirc; duyệt phương &aacute;n t&iacute;nh gi&aacute; b&aacute;n trong qu&yacute; ba năm nay. 483 căn c&ograve;n lại chưa kiểm định gi&aacute; b&aacute;n, c&ocirc;ng ty sẽ phải mời tổ chuy&ecirc;n vi&ecirc;n li&ecirc;n ng&agrave;nh đi kiểm tra thực địa, x&aacute;c định gi&aacute; b&aacute;n để ho&agrave;n th&agrave;nh việc t&iacute;nh gi&aacute; b&aacute;n trong qu&yacute; bốn.</p>\r\n\r\n<p style=\"text-align:justify\">Theo &ocirc;ng Dũng, với gần 11.000 căn nh&agrave; kh&ocirc;ng đủ điều kiện b&aacute;n do c&ograve;n nhiều vướng mắc, Sở X&acirc;y dựng sẽ ph&acirc;n loại cụ thể, x&aacute;c định r&otilde; tr&aacute;ch nhiệm của từng đơn vị li&ecirc;n quan v&agrave; đưa ra c&aacute;c giải ph&aacute;p phải thực hiện trong năm nay. Theo đ&oacute;, C&ocirc;ng ty Tr&aacute;ch nhiệm hữu hạn một th&agrave;nh vi&ecirc;n Quản l&yacute; v&agrave; Ph&aacute;t triển nh&agrave; H&agrave; Nội sẽ thực hiện loại khỏi danh s&aacute;ch b&aacute;n nh&agrave; 1.698 căn vướng quy hoạch, giải ph&oacute;ng mặt bằng; th&iacute; điểm b&aacute;n 20 biển số nh&agrave; trong khu phố cổ thuộc danh mục nh&oacute;m ba để r&agrave; so&aacute;t, đề xuất th&agrave;nh phố xem x&eacute;t, quyết định với 670 căn c&ograve;n lại; 1.592 căn li&ecirc;n quan tới vướng mắc kh&ocirc;ng k&yacute; được hợp đồng thu&ecirc; nh&agrave; v&agrave; 2.474 căn thiếu về thủ tục, hồ sơ...</p>\r\n\r\n<p style=\"text-align:justify\">Sở X&acirc;y dựng y&ecirc;u cầu C&ocirc;ng ty Tr&aacute;ch nhiệm hữu hạn một th&agrave;nh vi&ecirc;n Quản l&yacute; v&agrave; Ph&aacute;t triển nh&agrave; H&agrave; Nội b&aacute;o c&aacute;o cụ thể từng trường hợp tranh chấp, khiếu kiện, đ&ograve;i sở hữu nh&agrave; để giao Thanh tra Sở thực hiện theo Luật Khiếu nại. Cũng trong th&aacute;ng Tư n&agrave;y, C&ocirc;ng ty phải b&agrave;n giao hồ sơ 3.576 căn nh&agrave; cấp bốn cho ủy ban nh&acirc;n d&acirc;n c&aacute;c quận, huyện, thị x&atilde; cấp giấy chứng nhận theo quy định của ph&aacute;p luật về đất đai.</p>\r\n\r\n<p style=\"text-align:justify\">Theo đề xuất của Sở X&acirc;y dựng H&agrave; Nội, sau ng&agrave;y 30/12, nếu c&aacute;c cơ quan tự quản của trung ương v&agrave; H&agrave; Nội kh&ocirc;ng thực hiện b&agrave;n giao nh&agrave; đất, Sở sẽ kh&ocirc;ng tiếp nhận nh&agrave; do c&aacute;c cơ quan n&agrave;y chuyển giao. Tuy nhi&ecirc;n, để đẩy nhanh tiến độ b&aacute;n nh&agrave;, Sở đề nghị Bộ X&acirc;y dựng th&aacute;o gỡ một số vướng mắc về thời điểm v&agrave; thẩm quyền x&aacute;c nhận việc bố tr&iacute;, ph&acirc;n phối nh&agrave; ở cho c&aacute;n bộ c&ocirc;ng nh&acirc;n vi&ecirc;n của c&aacute;c cơ quan diện tự quản trước đ&acirc;y (trước ng&agrave;y 27/11/1992) l&agrave;m căn cứ để t&iacute;nh tiền b&aacute;n nh&agrave;; hướng dẫn về hệ số nh&agrave; ở chung cư c&oacute; tầng hầm, tầng lửng, thang m&aacute;y v&agrave; hệ số K ph&acirc;n lớp khi t&iacute;nh tiền sử dụng đất, việc &aacute;p dụng chế độ miễn giảm tiền sử dụng đất.</p>\r\n\r\n<p style=\"text-align:justify\">Theo MINH NGHĨA (TTXVN/VIETNAM+)</p>\r\n','go-vuong-cho-gan-11000-can-nha-khong-du-dieu-kien-ban','/web/uploads/images/150751baoxaydung_9.jpg','',NULL,NULL,'http://www.baoxaydung.com.vn/',0,NULL,NULL,NULL,NULL,0,1,NULL,1),(10,7,'Nhóm đại gia Sài Gòn lập quỹ trăm tỷ săn đất vùng ven','Bén duyên bất động sản hơn một thập niên, ông Hưng (ngụ quận 3, TP HCM) cùng nhóm bạn lập quỹ 70 tỷ đồng để săn đất tại các huyện Bình Chánh, Nhà Bè, Cần Giờ (khu Nam Sài Gòn), rồi bán lại, mỗi thương vụ thu lợi nhuận trung bình 15%','<p style=\"text-align:justify\">Loại h&igrave;nh bất động sản nh&oacute;m nh&agrave; đầu tư của &ocirc;ng Hưng ưa chuộng gồm c&oacute;: đất n&ocirc;ng nghiệp, đất ven s&ocirc;ng, đất mặt tiền biển v&agrave; nh&agrave; phố, biệt thự vườn. Nh&oacute;m tập trung mua c&aacute;c l&ocirc; đất c&oacute; hạ tầng đang triển khai hoặc cầu đường sắp được x&acirc;y dựng trong 12-24 th&aacute;ng tới.</p>\r\n\r\n<p style=\"text-align:justify\">&Ocirc;ng Hưng cho biết, chỉ trong 3 th&aacute;ng đầu năm, đội săn nh&agrave; đất của &ocirc;ng đ&atilde; giao dịch 5 thương vụ với tỷ suất lợi nhuận trung b&igrave;nh khoảng 15% mỗi lần mua b&aacute;n, c&aacute; biệt c&oacute; 2 giao dịch đạt tỷ suất sinh lời 25-30%.</p>\r\n\r\n<p style=\"text-align:justify\">C&oacute; số vốn c&ograve;n nhiều hơn cả đội bu&ocirc;n của &ocirc;ng Hưng, &ocirc;ng Bảo ngụ quận B&igrave;nh T&acirc;n, TP HCM huy động c&aacute;c nh&agrave; đầu tư c&aacute; nh&acirc;n th&acirc;n t&iacute;n lập quỹ hơn 100 tỷ đồng chuy&ecirc;n săn nh&agrave; đất ph&iacute;a T&acirc;y S&agrave;i G&ograve;n, đặc biệt l&agrave; địa b&agrave;n quận 8, 12, T&acirc;n Ph&uacute; v&agrave; hai huyện Củ Chi, H&oacute;c M&ocirc;n.</p>\r\n\r\n<p style=\"text-align:justify\">Mỗi th&agrave;nh vi&ecirc;n của nh&oacute;m đ&oacute;ng g&oacute;p theo dạng cổ phần, tối thiểu l&agrave; nửa tỷ đồng một suất, kh&ocirc;ng giới hạn vốn g&oacute;p, c&oacute; người năng lực t&agrave;i ch&iacute;nh tốt, đủ sức h&ugrave;n hạp dăm ba tỷ đồng. Nh&oacute;m nh&agrave; đầu tư của &ocirc;ng Bảo chuy&ecirc;n đ&agrave;m ph&aacute;n mua lại c&aacute;c quỹ đất từ nhỏ, lẻ đ&ocirc;i ba trăm m2, đến đất lớn v&agrave;i ha, đặc biệt ưa chuộng 2 nh&oacute;m t&agrave;i sản đang thế chấp ng&acirc;n h&agrave;ng v&agrave; tọa lạc ở khu vực c&oacute; biến động lớn về hạ tầng: cao tốc, cầu vượt, cầu bắc qua s&ocirc;ng, mở đường...</p>\r\n\r\n<p style=\"text-align:justify\">&Ocirc;ng Bảo tiết lộ, nh&oacute;m mới th&agrave;nh lập được 8 th&aacute;ng nhưng đ&atilde; th&acirc;u t&oacute;m được quỹ đất lớn l&ecirc;n đến v&agrave;i chục ngh&igrave;n m2. Đội chốt được trung b&igrave;nh 2-3 thương vụ mua đi b&aacute;n lại mỗi th&aacute;ng, lợi nhuận b&igrave;nh qu&acirc;n tr&ecirc;n dưới 20%. Nh&oacute;m c&oacute; một đội trưởng gi&agrave;u kinh nghiệm c&oacute; quyền quyết định cao nhất. C&aacute;c th&agrave;nh vi&ecirc;n c&ograve;n lại hoạt động hỗ trợ lẫn nhau phục vụ mục ti&ecirc;u chung từ việc săn nguồn h&agrave;ng, thẩm định gi&aacute;, khảo s&aacute;t thực tế, kiểm tra ph&aacute;p l&yacute; đến thương lượng, chốt mua chốt b&aacute;n.</p>\r\n\r\n<p style=\"text-align:justify\">Ghi nhận của&nbsp;<em>VnExpress</em>, việc c&aacute;c nh&agrave; đầu tư c&aacute; nh&acirc;n tại TP HCM lập quỹ từ h&agrave;ng chục tỷ đồng đến trăm tỷ để th&acirc;u t&oacute;m nh&agrave; đất đang rục rịch khởi động kể từ giai đoạn 2015-2016 v&agrave; trở n&ecirc;n rầm rộ v&agrave;o năm 2017.</p>\r\n\r\n<p style=\"text-align:justify\">C&aacute;c th&agrave;nh vi&ecirc;n trong nh&oacute;m nh&agrave; đầu tư n&agrave;y kh&aacute; đa dạng. C&oacute; người hoạt động ngo&agrave;i ng&agrave;nh địa ốc nhưng cũng c&oacute; kh&aacute; nhiều nh&acirc;n sự chủ chốt tại c&aacute;c c&ocirc;ng ty tư vấn đầu tư, đặc biệt nh&acirc;n sự ng&agrave;nh địa ốc chiếm tỷ lệ tr&ecirc;n 50%. C&aacute;c nh&agrave; đầu tư n&agrave;y chia sẻ, d&ograve;ng tiền t&iacute;ch lũy được thay v&igrave; gửi ng&acirc;n h&agrave;ng l&atilde;i suất kh&ocirc;ng đ&aacute;ng kể n&ecirc;n họ chọn phương &aacute;n gửi tiền v&agrave;o đất, vừa giữ gi&aacute; tốt vừa c&oacute; cơ hội sinh lời v&igrave; quỹ đất đ&ocirc; thị đang ng&agrave;y c&agrave;ng khan hiếm v&agrave; hạ tầng tại TP HCM được đầu tư mạnh mẽ.</p>\r\n\r\n<p style=\"text-align:justify\">Tổng gi&aacute;m đốc C&ocirc;ng ty cổ phần Tư vấn Đầu tư Ph&uacute; Vinh, Phan C&ocirc;ng Ch&aacute;nh đ&aacute;nh gi&aacute;, hiện tượng c&aacute;c nh&agrave; đầu tư c&aacute; nh&acirc;n lập nh&oacute;m bu&ocirc;n đất ven S&agrave;i G&ograve;n đ&atilde; bắt đầu xuất hiện ng&agrave;y c&agrave;ng nhiều hơn trong khoảng 2-3 năm trở lại đ&acirc;y. Tại c&aacute;c x&atilde; v&ugrave;ng ven ở S&agrave;i G&ograve;n, nhất l&agrave; c&aacute;c khu vực đang c&oacute; th&ocirc;ng tin được l&ecirc;n quận như Nh&agrave; B&egrave;, B&igrave;nh Ch&aacute;nh, H&oacute;c M&ocirc;n hoặc tại quận 9, Củ Chi, Cần Giờ đang xuất hiện rất nhiều m&ocirc; h&igrave;nh đầu tư nh&agrave; đất theo nh&oacute;m.</p>\r\n\r\n<p style=\"text-align:justify\">Nguy&ecirc;n nh&acirc;n s&acirc;u xa của l&agrave;n s&oacute;ng n&agrave;y, theo &ocirc;ng Ch&aacute;nh, xuất ph&aacute;t từ nhiều l&yacute; do đặc th&ugrave;. Thứ nhất, quỹ đất trung t&acirc;m ng&agrave;y c&agrave;ng hạn hẹp. Gi&aacute; cả đ&atilde; rất cao. Rất kh&oacute; để c&oacute; đột biến về gia tăng gi&aacute; trị nếu kh&ocirc;ng biết c&aacute;ch đầu tư v&agrave; c&oacute; chiến lược đ&uacute;ng. Trong t&igrave;nh huống n&agrave;y, đầu tư theo đội nh&oacute;m c&oacute; thể gi&uacute;p cho c&aacute;c đối t&aacute;c c&ugrave;ng khảo s&aacute;t, đ&aacute;nh gi&aacute; thực tế v&agrave; định gi&aacute; t&agrave;i sản chuẩn hơn, từ đ&oacute; đưa ra chiến lược ph&ugrave; hợp.</p>\r\n\r\n<p style=\"text-align:justify\">Nguy&ecirc;n nh&acirc;n thứ hai, nhiều đối thủ cạnh tranh sừng sỏ, nhất l&agrave; c&aacute;c nh&agrave; đầu tư chuy&ecirc;n nghiệp, cả trong v&agrave; ngo&agrave;i nước khiến cho thị trường trở n&ecirc;n chật chội với quỹ đất ng&agrave;y c&agrave;ng &iacute;t đi. Giống như một &quot;đại dương đỏ&quot;, nơi c&oacute; kh&aacute; &iacute;t những cơ hội thật sự cho nh&agrave; đầu tư c&aacute; nh&acirc;n với số vốn &iacute;t ỏi v&agrave; nguồn lực hạn chế. Trong bối cảnh đ&oacute;, lập nh&oacute;m bu&ocirc;n đất v&ugrave;ng ven l&agrave; một lựa chọn tốt hơn, ở g&oacute;c độ ph&ugrave; hợp nguồn lực.</p>\r\n\r\n<p style=\"text-align:justify\">Nguy&ecirc;n nh&acirc;n thứ ba, đầu tư theo nh&oacute;m dễ đ&oacute;n đầu cơ hội hơn đi một m&igrave;nh. Khi hạ tầng giao th&ocirc;ng tại S&agrave;i G&ograve;n được cải thiện với tốc độ c&agrave;ng nhanh, th&igrave; khoảng c&aacute;ch từ trung t&acirc;m tới c&aacute;c khu vực v&ugrave;ng ven sẽ được r&uacute;t ngắn. Cộng với tốc độ gia tăng d&acirc;n số ở mức nhanh ch&oacute;ng với hơn 200.000 người mỗi năm, kh&ocirc;ng bao l&acirc;u nữa quỹ đất cũng sẽ cạn kiệt. Đ&oacute; ch&iacute;nh l&agrave; l&uacute;c v&ugrave;ng ven trở n&ecirc;n đắt gi&aacute;. C&aacute;c khu vực trước đ&acirc;y 10 năm được xem l&agrave; v&ugrave;ng ven của S&agrave;i G&ograve;n như Ph&uacute; Mỹ Hưng, G&ograve; Vấp, Trung Sơn (B&igrave;nh Ch&aacute;nh), T&acirc;n Ph&uacute; nay như thế n&agrave;o mọi người cũng đ&atilde; thấy.</p>\r\n\r\n<p style=\"text-align:justify\">Ngo&agrave;i ra, suất đầu tư mỗi người thấp nhưng nếu g&oacute;p vốn c&ugrave;ng nhau để th&acirc;u t&oacute;m đất v&ugrave;ng ven giống như g&oacute;p gi&oacute; th&agrave;nh b&atilde;o. Ra ngoại th&agrave;nh S&agrave;i G&ograve;n, d&ograve;ng vốn c&agrave;ng lớn sẽ c&agrave;ng c&oacute; nhiều cơ hội. C&aacute;c l&yacute; do n&agrave;y l&agrave;m cho việc lập nh&oacute;m để bu&ocirc;n đất ở ngoại &ocirc; S&agrave;i G&ograve;n trở th&agrave;nh một xu hướng được quan t&acirc;m v&agrave; xem x&eacute;t nghi&ecirc;m t&uacute;c với c&aacute;c nh&agrave; đầu tư bất động sản c&aacute; nh&acirc;n.</p>\r\n\r\n<p style=\"text-align:justify\">Theo &ocirc;ng Ch&aacute;nh, m&ocirc; h&igrave;nh đầu tư theo nh&oacute;m c&oacute; cả ưu điểm v&agrave; nhược điểm. Ưu điểm l&agrave; mỗi nh&agrave; đầu tư chỉ cần bỏ ra một d&ograve;ng tiền vừa phải nhưng c&oacute; thể tham gia nhiều cơ hội lớn. Đi c&ugrave;ng nhau, c&aacute;c nh&oacute;m nh&agrave; đầu tư đứng trước cơ hội đ&oacute;n đầu c&aacute;c xu thế mới của thị trường, c&oacute; thể s&agrave;ng lọc được nhiều t&agrave;i sản c&ograve;n ở dạng tiềm năng.</p>\r\n\r\n<p style=\"text-align:justify\">Nhược điểm của m&ocirc; h&igrave;nh n&agrave;y, theo &ocirc;ng Ch&aacute;nh, l&agrave; c&oacute; thể xảy ra t&igrave;nh trạng bất đồng trong triển khai đầu tư mua b&aacute;n t&agrave;i sản. Chẳng hạn như, một người muốn mua hoặc b&aacute;n m&agrave; đội nh&oacute;m kh&ocirc;ng đồng &yacute; sẽ dẫn đến m&acirc;u thuẫn. Th&aacute;ch thức của việc đầu tư bất động sản v&ugrave;ng ven theo nh&oacute;m l&agrave; đất đai ở xa, kh&oacute; thăm nom, việc định gi&aacute;, khảo s&aacute;t gặp kh&ocirc;ng &iacute;t kh&oacute; khăn, trở ngại v&agrave; d&ograve;ng vốn nằm trong t&agrave;i sản l&agrave; nh&agrave;, đất c&oacute; thể bị tồn đọng một thời gian d&agrave;i nếu mọi việc kh&ocirc;ng đ&uacute;ng như kế hoạch t&iacute;nh to&aacute;n.</p>\r\n\r\n<p style=\"text-align:justify\">&quot;Để thực hiện tốt m&ocirc; h&igrave;nh kinh doanh n&agrave;y, c&aacute;c đội, nh&oacute;m đầu tư bất động sản cần c&oacute; một người nhiều kinh nghiệm đứng ra cầm trịch, được mọi th&agrave;nh vi&ecirc;n giao ph&oacute; quyền quyết định cao nhất nhằm đảm bảo lợi nhuận kỳ vọng v&agrave; an to&agrave;n cho d&ograve;ng vốn kh&aacute; lớn của nh&oacute;m&quot;, &ocirc;ng Ch&aacute;nh cho hay.</p>\r\n\r\n<p style=\"text-align:justify\">Theo Vũ L&ecirc;/VnExpress.net</p>\r\n','nhom-dai-gia-sai-gon-lap-quy-tram-ty-san-dat-vung-ven','/web/uploads/images/175801baoxaydung_16.jpg','',NULL,NULL,'',0,NULL,NULL,NULL,NULL,0,0,NULL,1),(11,7,'Đà Nẵng sẽ xây dựng Cảng cá lớn nhất miền Trung','UBND TP Đà Nẵng vừa ký quyết định phê duyệt tổng thể mặt bằng quy hoạch xây dựng chi tiết tỷ lệ 1/500 Cảng cá động lực thuộc Trung tâm nghề cá lớn TP Đà Nẵng.','<p style=\"text-align:justify\">Theo đ&oacute;, tổng diện t&iacute;ch đất quy hoạch l&agrave; 197.230m2, bao gồm 4 ph&acirc;n khu chức năng tại phường Thọ Quang, quận Sơn Tr&agrave;. Khu đất số 1 c&oacute; diện t&iacute;ch 20.777m2, dự kiến sẽ d&agrave;nh để n&acirc;ng cấp x&acirc;y dựng c&aacute;c cầu cảng hiện trạng th&agrave;nh cầu cảng chuy&ecirc;n d&ugrave;ng v&agrave; cầu cảng tổng hợp. Ph&iacute;a Bắc gi&aacute;p &Acirc;u thuyền Thọ Quang, ph&iacute;a Nam gi&aacute;p đường B&igrave;nh Than, ph&iacute;a T&acirc;y gi&aacute;p &Acirc;u thuyền Thọ Quang v&agrave; ph&iacute;a Đ&ocirc;ng gi&aacute;p Chợ đầu mối thủy sản Thọ Quang.</p>\r\n\r\n<p style=\"text-align:justify\">Khu đất số 2 c&oacute; diện t&iacute;ch 2.549m2, dự kiến để x&acirc;y dựng cải tạo lại chợ c&aacute; cũ. Ph&iacute;a Bắc gi&aacute;p Chợ đầu mối thủy sản Thọ Quang, ph&iacute;a Nam gi&aacute;p đường B&igrave;nh Than, ph&iacute;a T&acirc;y gi&aacute;p Cảng c&aacute; v&agrave; ph&iacute;a Đ&ocirc;ng gi&aacute;p đường V&acirc;n Đồn.</p>\r\n\r\n<p style=\"text-align:justify\">Khu đất số 3 c&oacute; diện t&iacute;ch 173.904m2, dự kiến x&acirc;y dựng ph&aacute;t triển th&agrave;nh khu đ&ocirc; thị kết hợp cảng quốc tế, c&oacute; ph&iacute;a Nam gi&aacute;p đường L&ecirc; Đức Thọ. Ph&iacute;a Đ&ocirc;ng, ph&iacute;a T&acirc;y, ph&iacute;a Bắc đều gi&aacute;p biển.</p>\r\n\r\n<p style=\"text-align:justify\">Khu đất số 4 c&oacute; diện t&iacute;ch 3.825m2, dự kiến để xử l&yacute; hạ tầng kỹ thuật tuyến cống đối lưu hiện trạng giao nhau với đường L&ecirc; Đức Thọ v&agrave; đường Hồ H&aacute;n Thương.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Nguyễn Nam</strong></p>\r\n','da-nang-se-xay-dung-cang-ca-lon-nhat-mien-trung','/web/uploads/images/100935baoxaydung_2.jpg','',NULL,NULL,'',0,NULL,NULL,NULL,NULL,0,1,NULL,1),(12,11,'10 lời khuyên giúp tiết kiệm chi phí xây nhà','Chọn thời điểm xây nhà vào mùa nắng ráo sẽ có nhiều thuận lợi: thời gian thi công không bị gián đoạn, giảm những chi phí phát sinh như phí thủ kho, bảo vệ, thất thoát vật tư, trượt giá, tiền điện...','<p>Để x&acirc;y một ng&ocirc;i nh&agrave; khang trang vừa với t&uacute;i tiền, bạn cần c&oacute; sự chuẩn bị thật chu đ&aacute;o trước khi bắt tay v&agrave;o mời thầu. Với mỗi giai đoạn cần c&acirc;n nhắc kỹ lưỡng, chặt chẽ, tu&acirc;n theo kế hoạch ban đầu th&igrave; sẽ tr&aacute;nh được chi ph&iacute; ph&aacute;t sinh trong qu&aacute; tr&igrave;nh x&acirc;y dựng. Nhiều năm c&ocirc;ng t&aacute;c trong lĩnh vực tư vấn, thiết kế v&agrave; thi c&ocirc;ng nh&agrave; gi&aacute; rẻ, kiến tr&uacute;c sư Huỳnh Xu&acirc;n Hải chia sẻ một số lời khuy&ecirc;n gi&uacute;p gia chủ chủ động hơn trong việc gi&aacute;m s&aacute;t qu&aacute; tr&igrave;nh thi c&ocirc;ng ng&ocirc;i nh&agrave; của m&igrave;nh, đồng thời tiết giảm tối đa chi ph&iacute; ph&aacute;t sinh.</p>\r\n\r\n<p>1. Trang bị kiến thức cơ bản về x&acirc;y dựng</p>\r\n\r\n<p>Để chuẩn bị l&agrave;m bất cứ việc g&igrave; n&ecirc;n trang bị cho m&igrave;nh vốn kiến thức cần c&oacute; v&agrave; một khoản kinh ph&iacute; dự tr&ugrave; ph&ugrave; hợp với điều kiện gia đ&igrave;nh. Những kiến thức n&agrave;y c&oacute; thể học hỏi từ bạn b&egrave; hoặc tham khảo s&aacute;ch b&aacute;o. Nhờ đ&oacute;, bạn sẽ chủ động hơn v&agrave; biết c&aacute;ch lựa chọn phương &aacute;n l&agrave;m việc ph&ugrave; hợp v&agrave; tiết kiệm nhất.</p>\r\n\r\n<p>2. Giai đoạn chuẩn bị</p>\r\n\r\n<p>Đ&acirc;y l&agrave; giai đoạn đầu ti&ecirc;n v&agrave; quan trọng nhất. Trong thời gian n&agrave;y, bạn cần lập một kế hoạch r&otilde; r&agrave;ng, c&agrave;ng chi tiết c&agrave;ng tốt, v&iacute; dụ dự đo&aacute;n tổng chi ph&iacute; x&acirc;y dựng, gi&aacute; vật liệu x&acirc;y dựng, nh&acirc;n c&ocirc;ng... Bạn c&oacute; thể tham khảo &yacute; kiến của những người từng x&acirc;y nh&agrave; trước đ&oacute; cũng như của bạn b&egrave;, nhất l&agrave; những người c&oacute; hiểu biết trong lĩnh vực x&acirc;y dựng. Việc tiếp thu n&ecirc;n c&oacute; chọn lọc những &yacute; kiến ph&ugrave; hợp với ho&agrave;n cảnh của ng&ocirc;i nh&agrave; m&igrave;nh dự định x&acirc;y.</p>\r\n\r\n<p>Trong giai đoạn n&agrave;y, bạn cần c&acirc;n nhắc giữa diện t&iacute;ch x&acirc;y dựng v&agrave; diện t&iacute;ch sinh hoạt, xem gia đ&igrave;nh c&oacute; bao nhi&ecirc;u người để thiết kế số lượng ph&ograve;ng hợp l&yacute;. C&acirc;n nhắc được vấn đề n&agrave;y sẽ gi&uacute;p bạn tr&aacute;nh t&igrave;nh trạng x&acirc;y thừa ph&ograve;ng, l&agrave;m l&atilde;ng ph&iacute; kh&ocirc;ng gian v&agrave; tốn chi ph&iacute; x&acirc;y dựng.</p>\r\n\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/web/uploads/images/0-4-565f61006d-3043ec1f96e9f34fafd885e353a3d8db.jpg\" style=\"height:280px; width:300px\" /></p>\r\n\r\n<p>3. Chọn mua mảnh đất dễ x&acirc;y</p>\r\n\r\n<p>Nếu chưa c&oacute; sẵn đất x&acirc;y nh&agrave;, bạn n&ecirc;n chọn mua mảnh đất n&agrave;o bằng phẳng, giao th&ocirc;ng thuận tiện v&agrave; gần những tiện nghi c&ocirc;ng cộng như trường học, chợ, si&ecirc;u thị&hellip; Sự thuận lợi về địa thế gi&uacute;p bạn giảm bớt rất nhiều chi ph&iacute; trong qu&aacute; tr&igrave;nh x&acirc;y dựng cũng như sinh sống sau n&agrave;y. N&ecirc;n tr&aacute;nh mua đất ở những nơi lồi l&otilde;m, nhiều đ&aacute;, nhiều nước, v&igrave; bạn sẽ mất c&ocirc;ng thu&ecirc; người dọn dẹp, lấp đất, đập đ&aacute; vừa mất c&ocirc;ng sức v&agrave; tốn th&ecirc;m chi ph&iacute;.</p>\r\n\r\n<p>Bạn n&ecirc;n tham khảo những người c&oacute; chuy&ecirc;n m&ocirc;n để gi&uacute;p bạn lựa chọn khu đất ở nơi g&ograve; đồi cao r&aacute;o, tr&aacute;nh những tầng địa chất yếu. Giả sử mua phải một mảnh đất ở khu vực c&oacute; tầng địa chất yếu, bạn phải tốn một khoản kinh ph&iacute; kh&ocirc;ng nhỏ cho việc gia cố m&oacute;ng như &eacute;p cọc, khoan cọc nhồi, đ&oacute;ng cừ tr&agrave;m. Ngược lại, đối với những nơi c&oacute; tầng địa chất cứng, việc gia cố m&oacute;ng sẽ đơn giản hơn nhiều, c&oacute; thể giảm từ 20% tới 30% gi&aacute; th&agrave;nh so với v&ugrave;ng đất yếu.</p>\r\n\r\n<p>4. Lựa chọn phong c&aacute;ch của ng&ocirc;i nh&agrave;</p>\r\n\r\n<p>Phong c&aacute;ch ng&ocirc;i nh&agrave; sẽ quyết định số tiền bạn bỏ ra để chi trả cho việc thi c&ocirc;ng. Nh&agrave; theo phong c&aacute;ch biệt thự, cổ điển cầu kỳ đương nhi&ecirc;n sẽ mất nhiều chi ph&iacute; x&acirc;y dựng hơn so với một ng&ocirc;i nh&agrave; mang phong c&aacute;ch hiện đại, đơn giản.&nbsp;</p>\r\n\r\n<p>Nếu bạn c&oacute; ng&acirc;n s&aacute;ch vừa phải, h&atilde;y chọn phong c&aacute;ch nh&agrave; hiện đại, với đường m&aacute;i v&agrave; cửa sổ đơn giản. Thay v&igrave; những kiểu thiết kế lạ mắt, kh&aacute;c thường, h&atilde;y chọn kiểu nh&agrave; h&igrave;nh chữ nhật với vị tr&iacute; x&acirc;y, sơn tường v&agrave; cảnh quan bắt mắt.</p>\r\n','10-loi-khuyen-giup-tiet-kiem-chi-phi-xay-nha','/web/uploads/images/0%20(4).jpg','',NULL,NULL,'',0,NULL,NULL,NULL,NULL,0,0,NULL,1),(13,7,'Tăng cường xử lý nghiêm các trường hợp vi phạm pháp luật về nhà ở','Bộ trưởng Bộ Xây dựng Phạm Hồng Hà vừa ký Công văn số 975/BXD-QLN gửi UBND các tỉnh, TP trực thuộc Trung ương về việc thực hiện quy định của pháp luật về quản lý sử dụng nhà chung cư','<p style=\"text-align:justify\">Theo đ&oacute;, để triển khai c&oacute; hiệu quả c&aacute;c quy định của ph&aacute;p luật li&ecirc;n quan đến việc quản l&yacute;, sử dụng nh&agrave; chung cư, nhằm giải quyết dứt điểm c&aacute;c tranh chấp, khiếu kiện ph&aacute;t sinh, Bộ X&acirc;y dựng đề nghị UBND c&aacute;c tỉnh, TP trực thuộc Trung ương chỉ đạo c&aacute;c cơ quan chức năng của địa phương khẩn trương thực hiện một số c&ocirc;ng việc:</p>\r\n\r\n<p style=\"text-align:justify\">Thứ nhất, tăng cường phổ biến, tuy&ecirc;n truyền c&aacute;c quy định của Luật Nh&agrave; ở số 65/2014/QH13, Nghị định số 99/2015/NĐ-CP, Th&ocirc;ng tư số 02/2016/TT-BXD v&agrave; Th&ocirc;ng tư số 28/2016/TT-BXD của Bộ X&acirc;y dựng.</p>\r\n\r\n<p style=\"text-align:justify\">Thứ 2, căn cứ v&agrave;o c&aacute;c quy định của ph&aacute;p luật về nh&agrave; ở, ban h&agrave;nh theo thẩm quyền c&aacute;c văn bản hướng dẫn, chỉ đạo, đ&ocirc;n đốc c&aacute;c cơ quan chức năng v&agrave; c&aacute;c tổ chức, c&aacute; nh&acirc;n c&oacute; li&ecirc;n quan tr&ecirc;n địa b&agrave;n thực hiện đ&uacute;ng c&aacute;c quy định của ph&aacute;p luật về nh&agrave; ở.</p>\r\n\r\n<p style=\"text-align:justify\">Thứ 3, tăng cường c&ocirc;ng t&aacute;c thanh tra, kiểm tra ph&aacute;t hiện v&agrave; xử l&yacute; nghi&ecirc;m c&aacute;c trường hợp vi phạm ph&aacute;p luật về nh&agrave; ở như kh&ocirc;ng b&agrave;n giao kinh ph&iacute; bảo tr&igrave; phần sở hữu chung, kh&ocirc;ng b&agrave;n giao hồ sơ nh&agrave; chung cư cho Ban quản trị nh&agrave; chung cư, kh&ocirc;ng tổ chức hoặc g&acirc;y kh&oacute; khăn trong việc tổ chức Hội nghị nh&agrave; chung cư, việc sử dụng căn hộ chung cư v&agrave;o mục đ&iacute;ch kh&ocirc;ng phải để ở, việc lấn chiếm kh&ocirc;ng gian, x&acirc;y dựng kh&ocirc;ng đ&uacute;ng với quy hoạch đ&atilde; được cơ quan c&oacute; thẩm quyền ph&ecirc; duyệt...</p>\r\n\r\n<p style=\"text-align:justify\">Thứ 4, chủ động giải quyết c&aacute;c vướng mắc, khiếu kiện, tranh chấp ph&aacute;t sinh trong qu&aacute; tr&igrave;nh quản l&yacute;, sử dụng nh&agrave; chung cư theo đ&uacute;ng c&aacute;c quy định của ph&aacute;p luật hiện h&agrave;nh để khắc phục t&igrave;nh trạng tranh chấp, khiếu kiện k&eacute;o d&agrave;i, vượt cấp l&ecirc;n cơ quan trung ương.</p>\r\n\r\n<p style=\"text-align:justify\">Đối với những nội dung cần nghi&ecirc;n cứu, sửa đổi, bổ sung cho ph&ugrave; hợp với t&igrave;nh h&igrave;nh thực tế, đề nghị UBND c&aacute;c tỉnh, TP trực Trung ương thuộc b&aacute;o c&aacute;o về Bộ X&acirc;y dựng để xem x&eacute;t sửa đổi, bổ sung theo thẩm quyền hoặc đề nghị cơ quan c&oacute; thẩm quyền nghi&ecirc;n cứu, sửa đổi, bổ sung cho ph&ugrave; hợp.</p>\r\n\r\n<p style=\"text-align:justify\">Theo Bộ X&acirc;y dựng, ng&agrave;y 25/11/2013, Quốc hội kh&oacute;a 13 đ&atilde; th&ocirc;ng qua Luật Nh&agrave; ở số 65/2014/QH13, trong đ&oacute; quy định nhiều nội dung li&ecirc;n quan đến việc quản l&yacute;, sử dụng nh&agrave; chung cư. Để hướng dẫn thi h&agrave;nh Luật Nh&agrave; ở, ng&agrave;y 20/10/2015 Ch&iacute;nh phủ đ&atilde; ban h&agrave;nh Nghị định số 99/2015/NĐ-CP quy định chi tiết v&agrave; hướng dẫn thi h&agrave;nh một số Điều của Luật Nh&agrave; ở v&agrave; Bộ X&acirc;y dựng cũng đ&atilde; ban h&agrave;nh Th&ocirc;ng tư số 02/2016/TT-BXD ng&agrave;y 15/2/2016 về Quy chế quản l&yacute;, sử dụng nh&agrave; chung cư. Hiện nay, nhiều địa phương c&oacute; nh&agrave; chung cư đ&atilde; chủ động triển khai thực hiện c&oacute; hiệu quả c&aacute;c quy định về quản l&yacute;, sử dụng nh&agrave; chung cư, từng bước đưa việc quản l&yacute;, sử dụng nh&agrave; chung cư đi v&agrave;o nề nếp, giải quyết được c&aacute;c vướng mắc, tranh chấp, khiếu kiện ph&aacute;t sinh v&agrave; tạo nếp sống văn minh, hiện đại cho người d&acirc;n tại c&aacute;c đ&ocirc; thị. Ng&agrave;y 16/6/2014, Bộ X&acirc;y dựng cũng đ&atilde; ban h&agrave;nh văn bản số 1334/BXD-QLN đề nghị c&aacute;c địa phương đ&ocirc;n đốc c&aacute;c tổ chức, c&aacute; nh&acirc;n trong việc thực hiện c&aacute;c quy định của ph&aacute;p luật c&oacute; li&ecirc;n quan đến việc quản l&yacute;, sử dụng nh&agrave; chung cư tr&ecirc;n địa b&agrave;n.</p>\r\n\r\n<p style=\"text-align:justify\">Tuy nhi&ecirc;n, qua theo việc thi h&agrave;nh c&aacute;c quy định về quản l&yacute;, sử dụng nh&agrave; chung cư trong thời gian vừa qua cho thấy, mặc d&ugrave; ph&aacute;p luật về nh&agrave; ở đ&atilde; c&oacute; c&aacute;c quy định cụ thể để xử l&yacute; c&aacute;c vướng mắc trong qu&aacute; tr&igrave;nh sử dụng nh&agrave; chung cư, nhưng tại một số địa phương vẫn c&ograve;n xảy ra c&aacute;c khiếu kiện, tranh chấp giữa người d&acirc;n với chủ đầu tư như việc tổ chức hội nghị nh&agrave; chung cư, việc th&agrave;nh lập v&agrave; quyết định c&ocirc;ng nhận Ban quản trị nh&agrave; chung cư, việc lựa chọn đơn vị quản l&yacute; vận h&agrave;nh nh&agrave; chung cư..., l&agrave;m ảnh hưởng đến t&igrave;nh h&igrave;nh an ninh trật tự của địa phương v&agrave; tạo dư luận kh&ocirc;ng tốt trong nh&acirc;n d&acirc;n.</p>\r\n\r\n<p style=\"text-align:justify\">Do vậy, Bộ X&acirc;y dựng đề nghị UBND c&aacute;c tỉnh, TP trực thuộc Trung ương chỉ đạo triển khai thực hiện c&aacute;c nội dung n&ecirc;u tr&ecirc;n.</p>\r\n\r\n<p style=\"text-align:justify\">Viễn Phong</p>\r\n','tang-cuong-xu-ly-nghiem-cac-truong-hop-vi-pham-phap-luat-ve-nha-o','/web/uploads/images/tin-tuc/182338baoxaydung_3.jpg','',NULL,NULL,'',0,NULL,NULL,NULL,NULL,0,0,NULL,1);

/*Table structure for table `tn_news_category` */

DROP TABLE IF EXISTS `tn_news_category`;

CREATE TABLE `tn_news_category` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `parent_id` int(11) DEFAULT '0',
  `name` varchar(255) DEFAULT NULL,
  `slug` varchar(255) DEFAULT NULL,
  `image` varchar(255) DEFAULT NULL,
  `content` text,
  `description` varchar(255) DEFAULT NULL,
  `sorted` int(11) DEFAULT '1',
  `status` tinyint(1) DEFAULT '1',
  PRIMARY KEY (`id`)
